    date: '22h33m',
    message: 'Texto da mensagem do cliente para o suporte, relacionado à ajuda ou outras requisições, mensagem de exemplo, teste etc...',
  },
  {
    id: '10',
    user: 'Suporte',
    date: '22h33m',
    message: 'Texto da mensagem de resposta',
  },
];

const App = () => {
  return (
    <SafeAreaView>
      <ScrollView style={styles.scroll}>
          <View style={{}}>
            <FlatList
            data={messages}
            renderItem={({ item }) => (
              <View style={styles.messageSended}>
                <View style={styles.messageSendedDataView}>
                  <Text style={styles.messageSendedData}>{item.user}</Text>
                  <Text style={styles.messageSendedData}>{item.date}</Text>
                </View>
                <View style={styles.messageSendedTextView}>
                  <Text style={styles.messageSendedText}>
                    {item.message}
                  </Text>
                </View>
              </View>
            )}
            inverted={true}
            keyExtractor={item => item.id}
            />
          </View>
        </ScrollView>
    </SafeAreaView>
  );
}

export default App;
