import { Dimensions, StyleSheet } from 'react-native';

const windowHeight = Dimensions.get('window').height;
const screenHeight = Dimensions.get('screen').height;
const windowWidth = Dimensions.get('screen').width;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#EFF0FF',
    // alignItems: 'center',
    justifyContent: 'space-between',
  },
  header: {
    position: 'absolute',
    top: 0,
    zIndex: 100,
    height: 90,
    paddingTop: 30,
    paddingHorizontal: 20,
    width: windowWidth,
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    borderBottomWidth: 1,
    borderBottomColor: '#ACC0F3',
    backgroundColor: '#EFF0FF'
    // backgroundColor: 'red'
  },
  text: {
    color: '#497BA9',
    fontFamily: 'Roboto',
    fontSize: 24,
    fontWeight: 'bold'
  },
  headerLogo: {
    height: 48,
    width: 48
  },
  chatView: {
    backgroundColor: 'yellow',
    flex: 1,
  },
  messageSended: {
    backgroundColor: '#1C71AE',
    borderRadius: 10,
    alignSelf: 'flex-end',
    paddingHorizontal: 10,
    width: 'auto',
    maxWidth: windowWidth / 1.4,
    marginHorizontal: 20,
    marginBottom: 20
  },
  messageSendedDataView: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    paddingVertical: 5,
    borderBottomWidth: 1,
    borderBottomColor: '#ACC0F3'
  },
  messageSendedData: {
    color: '#FFF',
    fontWeight: 'bold'
  },
  messageSendedTextView: {

  },
  messageSendedText: {
    color: '#FFF',
  },
  messageInput: {
    
  },
  input: {

  },
  button: {

  },

});

export default styles;
