import { StatusBar } from 'expo-status-bar';
import React from 'react';
// import { SafeAreaView } from 'react-native';
import { StyleSheet, FlatList, Text, SafeAreaView, ScrollView, View } from 'react-native';

import styles from './src/ChatScreen/styles.js';

import LoginScreen from './src/LoginScreen';
import ChatScreen from './src/ChatScreen';

const messages = [
  {
    id: '1',
    user: 'Cliente',
    date: '22h33m',
    message: 'Texto da mensagem do cliente para o suporte, relacionado à ajuda ou outras requisições, mensagem de exemplo, teste etc...',
  },
  {
    id: '2',
    user: 'Suporte',
    date: '22h33m',
    message: 'Texto da mensagem de resposta',
  },
  {
    id: '3',
    user: 'Cliente',
